mapper.select:
SELECT r2.* FROM
(
SELECT r1.*, TRUNC((rn -1) / #{showRow} + 1) AS
page FROM
(
SELECT ROWNUM rn, id, contents, ref, num FROM
(
SELECT * FROM res_meeting ORDER BY cre_date DESC
) WHERE ref = #{ref}
)r1
)r2 WHERE page =#{page}

mapper.update:
UPDATE meeting SET contents=#{contents}, title=#{title}, field=#{field}, area=#{area},
start_time=#{start_time}, end_time=#{end_time},
division=#{division}

	
		, map_name=#{map_name}
	

WHERE master=#{master}
